import React from "react";
import Risk5 from "./block3/risk5";
import Risk6 from "./block3/risk6";
import Risk7 from "./block3/risk7";
import Risk9 from "./block3/risk9";

class ThirdBlock extends React.Component {
   constructor(props) {
      super(props);
      this.state = {
         isClicked: true,
         ThirdData: {
             risk5Data: {
                 ans: 'none',
                 cat: -1,
                 number: -1
             },
             risk6Data: {
                 ans: 'none',
                 cat: -1,
                 number: -1
             },
             risk7Data: {
                ans: 'none',
                cat: -1,
                number: -1
            },
            risk9Data: {
                ans: 'none',
                cat: -1,
                number: -1
            }
         }
      };
      this.ClickToBlock = this.ClickToBlock.bind(this)
      this.handleRisk5Data = this.handleRisk5Data.bind(this)
      this.handleRisk6Data = this.handleRisk6Data.bind(this)
      this.handleRisk7Data = this.handleRisk7Data.bind(this)
      this.handleRisk9Data = this.handleRisk9Data.bind(this)
   }

   ClickToBlock = () => {
      this.setState(prevState => ({
         isClicked: !prevState.isClicked
      }));
   };

    handleRisk5Data = (data) => {
        this.setState((prevState) => ({
            ThirdData: {
                ...prevState.ThirdData,
                risk5Data: {
                    ans: data.ans,
                    cat: data.cat,
                    number: data.number,
                },
            },
        }));
    };

    handleRisk6Data = (data) => {
        this.setState((prevState) => ({
            ThirdData: {
                ...prevState.ThirdData,
                risk6Data: {
                    ans: data.ans,
                    cat: data.cat,
                    number: data.number,
                },
            },
        }));
    };

    handleRisk7Data = (data) => {
        this.setState((prevState) => ({
            ThirdData: {
                ...prevState.ThirdData,
                risk7Data: {
                    ans: data.ans,
                    cat: data.cat,
                    number: data.number,
                },
            },
        }));
    };
    handleRisk9Data = (data) => {
        this.setState((prevState) => ({
            ThirdData: {
                ...prevState.ThirdData,
                risk9Data: {
                    ans: data.ans,
                    cat: data.cat,
                    number: data.number,
                },
            },
        }));
    };

   render() {
      return (
         <div className="Risks">
            <h2 onClick={this.ClickToBlock}>Блок №3 : Субъект.</h2>
            {this.state.isClicked && (
               <div>
                  <Risk5/>
                  <Risk6/>
                  <Risk7/>
                  <Risk9/>
               </div>
            )}
         </div>
      )
   }
}

export default ThirdBlock
