import React from 'react';

class Risk1 extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      currentQuestion: 0,
      showAnswer: false,
    };
  }

  questions = [
    {
      number: 1,
      question: 'FirstQuestion in FirstRisk',
      answer: 'FirstAnswer in FirstRisk',
      ansCheck: false,
      help: false,
    },
    {
      number: 2,
      question: 'SecondQuestion in FirstRisk',
      answer: 'SecondAnswer in FirstRisk',
      ansCheck: false,
      help: false,
    },
    {
      number: 3,
      question: 'ThirdQuestion in FirstRisk',
      answer: 'ThirdAnswer in FirstRisk',
      ansCheck: false,
      help: false,
    },
  ];

  handleAnswer = (answer) => {
    if (answer === 'да') {
      const { currentQuestion } = this.state;
      if (currentQuestion < this.questions.length - 1) {
        this.setState({ currentQuestion: currentQuestion + 1 });
      }
    } else {
      this.setState({ showAnswer: true });
    }
  };

  render() {
    const { currentQuestion, showAnswer } = this.state;
    return (
      <div className="Risk">
        {currentQuestion < this.questions.length ? (
          <div className="Question">
            <p>{this.questions[currentQuestion].question}</p>
            <button onClick={() => this.handleAnswer('да')}>Да</button>
            <button onClick={() => this.handleAnswer('нет')}>Нет</button>
          </div>
        ) : null}
        {showAnswer && (
          <div className="Answer">
            <p>{this.questions[currentQuestion - 1].answer}</p>
          </div>
        )}
      </div>
    );
  }
}

export default Risk1;
