import React from "react";

class Risk1 extends React.Component{
    constructor(props) {
        super(props);
        this.state = {
          currentQuestion: 0,
          nextQuestion: true
        };
        this.handleAnswer = this.handleAnswer.bind(this)
    }
    quests = [
        {
            number: 1,
            question: 'FirstQuestion in FirstRisk',
            answer: 'FirstAnswer in FirstRisk',
            ansCheck: false,
            help: false
        },
        {
            number: 2,
            question: 'SecondQuestion in FirstRisk',
            answer: 'SecondAnswer in FirstRisk',
            ansCheck: false,
            help: false
        },
        {
            number: 3,
            question: 'ThirdQuestion in FirstRisk',
            answer: 'ThirdAnswer in FirstRisk',
            ansCheck: false,
            help: false
        }
    ]
    handleAnswer = (answer) => {
        const { currentQuestion } = this.state;
        if (answer === 'да' && currentQuestion < this.quests.length - 1) {
          this.setState({ currentQuestion: currentQuestion + 1 });
        } else {
          this.setState({ nextQuestion: false });
        }
    };

    render(){
        const { currentQuestion, nextQuestion } = this.state;
        return(
            <div className="Risk">
        {showNextQuestion ? (
          <div className="Question">
            <p>{this.questions[currentQuestion].question}</p>
            <button onClick={() => this.handleAnswer("да")}>Да</button>
            <button onClick={() => this.handleAnswer("нет")}>Нет</button>
          </div>
        ) : (
          <div>
            {currentQuestion < this.questions.length - 1 ? (
              <div className="Question">
                <p>{this.questions[currentQuestion + 1].question}</p>
                <button onClick={() => this.handleAnswer("да")}>Да</button>
                <button onClick={() => this.handleAnswer("нет")}>Нет</button>
              </div>
            ) : (
              <div>
                <p>{this.questions[currentQuestion].answer}</p>
              </div>
            )}
          </div>
        )}
      </div>
        )
    }
}

export default Risk1
