import React from "react";
import { BsQuestionCircleFill } from 'react-icons/bs';

class Instrument extends React.Component {
   render() {
      const {
         showBlocks,
         showCheck,
         items,
         visible,
         handleContinue2,
         handleCheckboxChange,
         handleMouseEnter,
         handleMouseLeave,
         tooltips,
         allChecked
      } = this.props;

      return (
         <div className="App">
            {showBlocks ? (
               <div>
                  <h3>Подтвердите наличие всех необходимых документов!</h3>
                  <div>
                     {items.map((item, index) => (
                        <div key={index} className="Docs">
                           <div
                              className="Tooltip"
                              onMouseEnter={() => handleMouseEnter(index)}
                              onMouseLeave={() => handleMouseLeave(index)}
                              style={{
                                 display: visible[index] ? "block" : "none"
                              }}
                           >
                              {tooltips[index]}
                           </div>
                           <BsQuestionCircleFill
                              className="QuestIcon"
                              onMouseEnter={() => handleMouseEnter(index)}
                              onMouseLeave={() => handleMouseLeave(index)}
                           />
                           <input
                              type="checkbox"
                              className="DocCheckBox"
                              checked={item.checked}
                              onChange={() => handleCheckboxChange(index)}
                           />
                           <label className="Docs">{item.label}</label>
                        </div>
                     ))}
                     <button disabled={!allChecked} onClick={handleContinue2}>
                        Продолжить
                     </button>
                  </div>
               </div>
            ) : (
               <div>
                  {/* Render the Blocks component here */}
               </div>
            )}
         </div>
      );
   }
}

export default Instrument;
