import React from "react";

class Risk4 extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            isClicked: false
        };
        this.ClickToBlock = this.ClickToBlock.bind(this);
    }

    ClickToBlock = (event) => {
        if (event.target.tagName !== 'BUTTON') {
            this.setState(prevState => ({
                isClicked: !prevState.isClicked
            }));
        }
    };

    render() {
        return (
           <div className="Risk" onClick={this.ClickToBlock}>
               <h3>Риск №4: Экология</h3>
               {this.state.isClicked && (
                  <div>
                      <div className="Question">
                          <p>Вы ознакомлены с информацией об экологической ситуации земельного участка?</p>
                          <button>Да</button>
                          <button>Нет</button>
                      </div>
                  </div>
               )}
           </div>
        );
    }
}

export default Risk4;
