import React from 'react';
import { Page, Text, Document, StyleSheet, Font } from '@react-pdf/renderer';

const absolutePath = 'C:/Windows/Fonts/arial.ttf';

Font.register({
    family: 'Arial',
    fonts: [
      { src: absolutePath, fontWeight: 'normal' }
    ],
  });

const Report = () => {
    return (
      <Document>
        <Page style={styles.page}>
          
            <Text style={styles.checkIcon}>Hellosads!</Text>
            <Text style={styles.conclusionTitle}>Привет, мир!!!</Text>
                  
        </Page>
      </Document>
    );
  };
  
  const styles = StyleSheet.create({
    page: {
      fontFamily: 'Arial',
      paddingTop: 30,
      paddingBottom: 30,
      paddingHorizontal: 30,
    },
    checkIcon: {
      fontSize: 36,
      color: 'green',
    },
    conclusionTitle: {
      fontSize: 18,
    },
    dashIcon: {
      fontSize: 36,
      color: 'orange',
    },
    riskTitle: {
      fontSize: 20,
      marginBottom: 10,
    },
    xIcon: {
      fontSize: 36,
      color: 'red',
    },
    recTitle: {
      fontSize: 16,
      fontWeight: 'bold',
      marginBottom: 10,
    },
  });
  
  export default Report;
