import React from "react";
import Risk1 from "./block1/risk1";
import Risk10 from "./block1/risk10";

class FirstBlock extends React.Component{
    constructor(props) {
        super(props);
        this.state = {
            isClicked: true,
            FirstData: {
                risk1Data: {
                    ans: 'none',
                    cat: -1,
                    number: -1
                },
                risk10Data:{
                    ans: 'none',
                    cat: -1,
                    number: -1
                }
            }
        };
        this.ClickToBlock = this.ClickToBlock.bind(this)
    }
    
    ClickToBlock = () => {
        this.setState(prevState => ({
          isClicked: !prevState.isClicked
        }));
    };

    handleRisk1Data = (data) => {
        this.setState({
            FirstData:{
                risk1Data: {
                    ans: data.ans,
                    cat: data.cat,
                    number: data.number
                }
            }
        });
    };
    
    handleRisk10Data = (data) => {
        this.setState({
            FirstData:{
                risk10Data: {
                    ans: data.ans,
                    cat: data.cat,
                    number: data.number
                }
            }
        });
    };

    render(){
        return(
            <div className="Risks">
                <h2 onClick={this.ClickToBlock}>Блок №1 : Документы.</h2>
                {this.state.isClicked && (
                    <div>
                        <Risk1 onData={this.handleRisk1Data}/>
                        <Risk10 onData={this.handleRisk10Data} />
                    </div>
                )}
            </div>
        )
    }
}

export default FirstBlock
