import React from "react";
import {BiSolidRightArrow} from "react-icons/bi"
import { BsQuestionCircleFill } from 'react-icons/bs'

class Risk2 extends React.Component{
    constructor(props) {
        super(props);
        this.state = {
            ans: [false, false, false],
            click: [false, false, false],
            isClicked: false,
            visible: [false],
            riskanswer: {
                ans: 'none',
                cat: -1,
                number: -1
             }
        };
        this.handleAnswer1 = this.handleAnswer1.bind(this)
        this.handleAnswer2 = this.handleAnswer2.bind(this)
        this.handleAnswer3 = this.handleAnswer3.bind(this)
        this.ClickToBlock = this.ClickToBlock.bind(this)
        this.handleMouseEnter = this.handleMouseEnter.bind(this);
        this.handleMouseLeave = this.handleMouseLeave.bind(this);
    }
    quests = [
        {
            number: 1,
            question: 'Определены ли границы земельного участка?',
            answer: 'Риска нет.',
            ansCheck: false,
            help: false
        },
        {
            number: 2,
            question: 'Проведено ли межевание участка?',
            answer: 'Риск есть.',
            ansCheck: false,
            help: false
        },
        {
            number: 3,
            question: 'Вы уверены, что межевание соответствует выписке ЕГРН?',
            answer: 'Риск есть',
            ansCheck: false,
            help: false
        }
    ]
    ClickToBlock = (event) => {
        if (event.target.tagName !== 'BUTTON') {
            this.setState(prevState => ({
                isClicked: !prevState.isClicked
            }));
        }
    };
    handleAnswer1 = (answer) => {
        this.setState(prevState => ({
            click: prevState.click.map((item, index) =>
               index === 0 ? true : item
            )
        }));
        if (answer === 'да') {
            this.setState(prevState => ({
                ans: prevState.ans.map((item, index) =>
                   index === 0 ? false : item
                )
            }));
            this.setState({ 
                riskanswer:{
                   ans: 'Риска нет',
                   cat: 0,
                   number: 0
                }
             });
        }
        else {
            this.setState(prevState => ({
                ans: prevState.ans.map((item, index) =>
                   index === 0 ? true : item
                )
            }));

        }
    };
    handleAnswer2 = (answer) => {
        this.setState(prevState => ({
            click: prevState.click.map((item, index) =>
               index === 1 ? true : item
            )
        }));
        if (answer === 'да') {
            this.setState(prevState => ({
                ans: prevState.ans.map((item, index) =>
                   index === 1 ? false : item
                )
            }));
        } else {
            this.setState(prevState => ({
                ans: prevState.ans.map((item, index) =>
                   index === 1 ? true : item
                )
            }));
            this.setState({ 
                riskanswer:{
                   ans: 'Риск есть',
                   cat: 1,
                   number: 1
                }
             });

        }
    };
    handleAnswer3 = (answer) => {
        this.setState(prevState => ({
            click: prevState.click.map((item, index) =>
               index === 2 ? true : item
            )
        }));
        if (answer === 'да') {
            this.setState(prevState => ({
                ans: prevState.ans.map((item, index) =>
                   index === 2 ? false : item
                )
            }));
            this.setState({ 
                riskanswer:{
                   ans: 'Риска нет',
                   cat: 0,
                   number: 0
                }
             });
        } else {
            this.setState(prevState => ({
                ans: prevState.ans.map((item, index) =>
                   index === 2 ? true : item
                )
            }));
            this.setState({ 
                riskanswer:{
                   ans: 'Риск есть',
                   cat: 1,
                   number: 2
                }
             });

        }
    };
    handleMouseEnter = (index) => {
        const updatedVisible = [...this.state.visible];
        updatedVisible[index] = true;
        this.setState({ visible: updatedVisible });
     };
  
     handleMouseLeave = (index) => {
        const updatedVisible = [...this.state.visible];
        updatedVisible[index] = false;
        this.setState({ visible: updatedVisible });
     };

     componentDidUpdate(prevProps, prevState) {
        if (this.state.riskanswer !== prevState.riskanswer) {
           this.props.onData(this.state.riskanswer);
        }
     }

    render(){
        const { ans, click, isClicked, visible } = this.state;
        const tooltips = [
            `<b>Межевание</b> - это работа кадастрового инженера, которая позволяет определить границы земельного участка в межевом плане и внести данные в ЕГРН. `
         ];
        return(
            <div className="Risk">
                <div className="RiskName">
                    <BiSolidRightArrow
                        className={`RiskIcon ${isClicked ? 'rotated' : ''}`}
                        onClick={this.ClickToBlock}
                    />
                    <h3>Границы объекта</h3>
                </div>
                {this.state.isClicked && (
                    <div>
                        <div className="Question">
                            <p>{this.quests[0].question}</p>
                            <button className={`ans-btn ${click[0] && !ans[0] ? 'active' : ''}`} onClick={() => this.handleAnswer1('да')}>Да</button>
                            <button className={`ans-btn ${click[0] && ans[0] ? 'active' : ''}`} onClick={() => this.handleAnswer1('нет')}>Нет</button>
                        </div>
                        {click[0] && (
                            <div>
                                {!ans[0] ? (
                                    <div className="Answer">
                                        <p>{this.quests[0].answer}</p>
                                    </div>
                                ) : (
                                    <div className="Question">
                                        <div className="QuestTool">
                                            <p>{this.quests[1].question}</p> 
                                            <BsQuestionCircleFill
                                            className="QuestIcon"
                                            onMouseEnter={() => this.handleMouseEnter(0)}
                                            onMouseLeave={() => this.handleMouseLeave(0)}
                                            />
                                            <div className="ToolContainer">
                                            <div
                                                    className="Tooltip"
                                                    onMouseEnter={() => this.handleMouseEnter(0)}
                                                    onMouseLeave={() => this.handleMouseLeave(0)}
                                                    style={{
                                                        display: visible[0] ? "block" : "none"
                                                    }}
                                                    dangerouslySetInnerHTML={{ __html: tooltips[0] }}
                                            >
                                            </div>
                                            </div>
                                        </div>
                                        <button className={`ans-btn ${click[1] && !ans[1] ? 'active' : ''}`} onClick={() => this.handleAnswer2('да')}>Да</button>
                                        <button className={`ans-btn ${click[1] && ans[1] ? 'active' : ''}`} onClick={() => this.handleAnswer2('нет')}>Нет</button>
                                    </div>
                                )}
                            </div>
                        )}
                        {click[1] && ans[0] && (
                            <div>
                                {!ans[1] ? (
                                    <div className="Question">
                                        <p>{this.quests[2].question}</p>
                                        <button className={`ans-btn ${click[2] && !ans[2] ? 'active' : ''}`} onClick={() => this.handleAnswer3('да')}>Да</button>
                                        <button className={`ans-btn ${click[2] && ans[2] ? 'active' : ''}`} onClick={() => this.handleAnswer3('нет')}>Нет</button>
                                    </div>
                                ) : (
                                    <div className="Answer">
                                        <p>{this.quests[1].answer}</p>
                                    </div>
                                )}
                            </div>
                        )}
                        {click[2] && ans[0] && !ans[1] &&(
                            <div>
                                {!ans[2] ? (
                                    <div className="Answer">
                                        <p>Риска нет.</p>
                                    </div>
                                ) : (
                                    <div className="Answer">
                                        <p>{this.quests[2].answer}</p>
                                    </div>
                                )}
                            </div>
                        )}
                    </div>
                )}
            </div>
        )
    }
}

export default Risk2
