// App.js
import React from "react";
import Navbar from "./components/navbar";
import Instrument from "./components/instrument";
import WelcomePage from "./components/startpage";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";

function App() {
   const [showBlocks, setShowBlocks] = React.useState(true);
   const [showCheck, setShowCheck] = React.useState(true);
   const [items, setItems] = React.useState([
      { label: 'Копия/Фото паспорта продавца', checked: false },
      { label: 'Выписка из ЕГРН', checked: false }
   ]);
   const [visible, setVisible] = React.useState([false, false]);

   const handleCheckboxChange = (index) => {
      const updatedItems = [...items];
      updatedItems[index].checked = !updatedItems[index].checked;
      setItems(updatedItems);
   };

   const handleContinue1 = () => {
      setShowCheck(false);
   };

   const handleContinue2 = () => {
      setShowBlocks(false);
   };

   const handleMouseEnter = (index) => {
      const updatedVisible = [...visible];
      updatedVisible[index] = true;
      setVisible(updatedVisible);
   };

   const handleMouseLeave = (index) => {
      const updatedVisible = [...visible];
      updatedVisible[index] = false;
      setVisible(updatedVisible);
   };

   return (
      <div>
         <Router>
            <Navbar />
            <Routes>
               <Route path="/" element={<WelcomePage handleContinue1={handleContinue1} />} />
               <Route
                  path="/instrument"
                  element={
                     <Instrument
                        showBlocks={showBlocks}
                        showCheck={showCheck}
                        items={items}
                        visible={visible}
                        handleContinue2={handleContinue2}
                        handleCheckboxChange={handleCheckboxChange}
                        handleMouseEnter={handleMouseEnter}
                        handleMouseLeave={handleMouseLeave}
                     />
                  }
               />
            </Routes>
         </Router>
      </div>
   );
}

export default App;
