import React from "react"
import Navbar from "./components/navbar"
import Blocks from "./components/blocks"
import Welcome from "./components/welcome";
import {BsQuestionCircleFill} from 'react-icons/bs'
import Instrument from "./components/insrument"
import Mainpage from "./components/mainpage"

import {
   BrowserRouter,
   Routes,
   Route,
} from 'react-router-dom';

class App extends React.Component {
   constructor(props) {
      super(props);
      this.state = {
         showBlocks: true,
         showCheck: true,
         items: [
            {label: 'Копия/Фото паспорта продавца', checked: false},
            {label: 'Выписка из ЕГРН', checked: false}
         ],
         visible: [false, false]
      };
      this.handleContinue1 = this.handleContinue1.bind(this)
      this.handleContinue2 = this.handleContinue2.bind(this)
      this.handleCheckboxChange = this.handleCheckboxChange.bind(this)
      this.handleMouseEnter = this.handleMouseEnter.bind(this)
      this.handleMouseLeave = this.handleMouseLeave.bind(this)
   }

   handleCheckboxChange = (index) => {
      const {items} = this.state;
      const updatedItems = [...items];
      updatedItems[index].checked = !updatedItems[index].checked;
      this.setState({items: updatedItems});
   };

   handleContinue1 = () => {
      this.setState({showCheck: false});
   };

   handleContinue2 = () => {
      this.setState({showBlocks: false});
   };

   handleMouseEnter = (index) => {
      const updatedVisible = [...this.state.visible];
      updatedVisible[index] = true;
      this.setState({visible: updatedVisible});
   };

   handleMouseLeave = (index) => {
      const updatedVisible = [...this.state.visible];
      updatedVisible[index] = false;
      this.setState({visible: updatedVisible});
   };

   render() {
      const {showBlocks, items, visible} = this.state;
      const allChecked = items.every((item) => item.checked);
      const tooltips = ["Подсказка 1", "Подсказка 2"];

      return (
         <BrowserRouter>
            <div>
               <Navbar/>
               <div className="App">
                  <Routes>
                     <Route path="/" element={<MainPage onContinue1={this.handleContinue1}/>}/>
                     <Route
                        path="/instrument"
                        element={
                           <Instrument
                              items={items}
                              tooltips={tooltips}
                              allChecked={allChecked}
                              handleCheckboxChange={this.handleCheckboxChange}
                              handleContinue2={this.handleContinue2}
                           />
                        }
                     />
                     {/* Добавьте другие маршруты, если необходимо */}
                  </Routes>
               </div>
            </div>
         </BrowserRouter>
      );
   }
}

export default App
