import React from "react"
import Navbar from "./components/navbar"

const questions = [
    {
      id: 1,
      question: 'Вопрос 1: Вы любите программировать?',
    },
    {
      id: 2,
      question: 'Вопрос 2: Вам нравится React?',
    },
    {
      id: 3,
      question: 'Вопрос 3: Вы готовы изучать новые технологии?',
    },
  ];

class App extends React.Component{
    constructor(props) {
        super(props);
        this.state = {
          currentQuestion: 0,
          showQuestion: true,
        };
        this.handleAnswer.bind(this)
        this.handleContinue.bind(this)
    }
    handleContinue = () => {
        this.setState({ showQuestion: true });
    };
    
    handleAnswer = (answer) => {
        const { currentQuestion } = this.state;
        if (answer === 'да' && currentQuestion < questions.length - 1) {
            this.setState({ currentQuestion: currentQuestion + 1 });
        } 
        else {
            this.setState({ showQuestion: false });
        }
    };

    render(){
        const { currentQuestion, showQuestion } = this.state;
        return(
            <div>
                <Navbar /> 
            <div className="App">
            {showQuestion ? (
            <div>
                <h1>Добро пожаловать!</h1>
                <button onClick={this.handleContinue}>Продолжить</button>
            </div>
            ) : (
            <div>
                <h2>{questions[currentQuestion].question}</h2>
                <button onClick={() => this.handleAnswer('да')}>Да</button>
                <button onClick={() => this.handleAnswer('нет')}>Нет</button>
            </div>
            )}
        </div>
            </div>
        )
    }
}

export default App
