import React from 'react';

class Risk1 extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      currentQuestion: 0,
      answers: [],
    };
  }

  questions = [
    {
      number: 1,
      question: 'FirstQuestion in FirstRisk',
      answer: 'FirstAnswer in FirstRisk',
      ansCheck: false,
      help: false,
    },
    {
      number: 2,
      question: 'SecondQuestion in FirstRisk',
      answer: 'SecondAnswer in FirstRisk',
      ansCheck: false,
      help: false,
    },
    {
      number: 3,
      question: 'ThirdQuestion in FirstRisk',
      answer: 'ThirdAnswer in FirstRisk',
      ansCheck: false,
      help: false,
    },
  ];

  handleAnswer = (answer) => {
    const { currentQuestion, answers } = this.state;
    const updatedAnswers = [...answers, answer];
    this.setState({ answers: updatedAnswers });

    if (answer === 'да' && currentQuestion < this.questions.length - 1) {
      this.setState({ currentQuestion: currentQuestion + 1 });
    }
  };

  render() {
    const { currentQuestion, answers } = this.state;
    const question = this.questions[currentQuestion];

    return (
      <div className="Risk">
        {answers.map((answer, index) => (
          <div key={index} className="Answer">
            <p>{this.questions[index].question}</p>
            <p>Ответ: {answer}</p>
          </div>
        ))}
        {currentQuestion < this.questions.length && (
          <div className="Question">
            <p>{question.question}</p>
            <button onClick={() => this.handleAnswer('да')}>Да</button>
            <button onClick={() => this.handleAnswer('нет')}>Нет</button>
          </div>
        )}
      </div>
    );
  }
}

export default Risk1;
