import React from 'react';

class Risk1 extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      currentQuestion: 0,
    };
  }

  quests = [
    {
      number: 1,
      question: 'FirstQuestion in FirstRisk',
      answer: 'FirstAnswer in FirstRisk',
      ansCheck: false,
      help: false,
    },
    {
      number: 2,
      question: 'SecondQuestion in FirstRisk',
      answer: 'SecondAnswer in FirstRisk',
      ansCheck: false,
      help: false,
    },
    {
      number: 3,
      question: 'ThirdQuestion in FirstRisk',
      answer: 'ThirdAnswer in FirstRisk',
      ansCheck: false,
      help: false,
    },
  ];

  handleAnswer = (answer) => {
    const { currentQuestion } = this.state;
    if (answer === 'да' && currentQuestion < this.quests.length - 1) {
      this.setState({ currentQuestion: currentQuestion + 1 });
    }
  };

  render() {
    const { currentQuestion } = this.state;
    return (
      <div className="Risk">
        {this.quests.map((question, index) => {
          if (index <= currentQuestion) {
            return (
              <div key={question.number} className="Question">
                <p>{question.question}</p>
                {index === currentQuestion && (
                  <div>
                    <button onClick={() => this.handleAnswer('да')}>Да</button>
                    <button onClick={() => this.handleAnswer('нет')}>Нет</button>
                  </div>
                )}
                {index < currentQuestion && (
                  <div>
                    <p>{question.answer}</p>
                  </div>
                )}
              </div>
            );
          } else {
            return null;
          }
        })}
      </div>
    );
  }
}

export default Risk1;
