import React from "react"
import Navbar from "./components/navbar"

const questions = [
    {
      id: 1,
      question: 'Вопрос 1: Вы любите программировать?',
    },
    {
      id: 2,
      question: 'Вопрос 2: Вам нравится React?',
    },
    {
      id: 3,
      question: 'Вопрос 3: Вы готовы изучать новые технологии?',
    },
  ];

class App extends React.Component{
    constructor(props) {
        super(props);
        this.state = {
          showBlocks: false,
        };
        this.handleAnswer.bind(this)
        this.handleContinue.bind(this)
    }

    handleContinue = () => {
        this.setState({ showBlocks: true });
    };

    render(){
        const { showQuestion } = this.state;
        return(
            <div>
                <Navbar /> 
                <div className="App">
                    {showQuestion ? (
                        <div>
                            <h1>Добро пожаловать!</h1>
                            <button onClick={this.handleContinue}>Продолжить</button>
                        </div>
                    ) : (
                        <div>
